/* eslint-disable react-hooks/exhaustive-deps */
import { React, useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getVideogameDetail, clean } from "../../Redux/actions";
import Navbar from "../NavBar/NavBar";
import photo from "../../img/conecting.gif";
import { NavLink, useParams } from "react-router-dom";
import "./gamedetail.css";

export default function GameDetails(props) {

  const [carga, setCarga] = useState(true);
  const {idVideogame} = useParams() //rutas dinamicas, Podemos acceder a cualquier parámetro de ruta de una ruta declarada con su componente asociado usando el hook useParams.
  const dispatch = useDispatch()

  React.useEffect(() => {
      dispatch(getVideogameDetail(idVideogame)).then(() => setCarga(false))
  }, [dispatch, idVideogame])

  const details = useSelector(state => state.videogame)
  //console.log(details)

  if (carga) {
      return <Loading />;
    }

  var regex = /(<([^>]+)>)/gi; 
  

  
  return ( 
    <div className="container-detail">
      
      <div className="details-div">
        {gameDetails ? ( 
          <div>
            <h3 className="title">{gameDetails.name}</h3>
            {gameDetails.background_image ? ( 
              <div className="div-img">
                <img src={gameDetails.background_image} alt="Videogame"></img>
              </div>
            ) : ( 
              <div className="div-img">
                <img src={photo} alt="Videogame"></img>
              </div>
            )}
            {
              <p>
                <strong>Release Date</strong>:{" "}
                {`${gameDetails.releaseDate || "None"}`} 
               
              </p>
            }
            <p>
              <strong>Rating</strong>: ★ {`${gameDetails.rating}`} 
            </p>
            {gameDetails.description && 
            gameDetails.genres && 
            gameDetails.platforms ? ( 
              <div className="div-descr">
                {
                  <p className="descripcion">
                    {gameDetails.description.replace(/(<([^>]+)>)/gi, "")} 
                    
                  </p>
                }
                {
                  <p>
                    <strong>Genres</strong>:{" "}
                    {`${gameDetails.genres.join(", ")}`} 
                    
                  </p>
                }
                {
                  <p>
                    <strong>Platforms</strong>:{" "}
                    {`${
                      typeof gameDetails.platforms === "string" 
                        ? gameDetails.platforms 
                        : gameDetails.platforms.join(", ") 
                    }`}
                  </p>
                }

                 <NavLink to="/videogames"> 
                  <button>VOLVER</button>
                 </NavLink> 
              </div>
            ) : ( 
              <h1>Cargando</h1>
            )}
          </div>
        ) : ( 
          <h1>Cargando</h1>
        )}
      </div>
    </div>
  );
}
